import React, {useContext} from 'react'
import axiosWithAuth from '../axiosWithAuth'
import {useHistory } from 'react-router-dom'
import { DinerContext } from '../../Contexts/DinerContext'

const {push} =useHistory;



export default function OpTruckCard(props) {
    const [operatorTrucks] = useContext(DinerContext)
    console.log(operatorTrucks)
    const handleDelete = e => {
        axiosWithAuth().delete(`/api/trucks/${props.id}`)
        .then(res =>{
            console.log(res);
            push(`/Operator/Dashboard/trucks/add/${props.id}`)
    
        })
        .catch (err =>{
            console.log(err,'no data coming in')
        })
    }


    return (
        <div className='truckCard'>
            <h1>{props.truckName}</h1>
            <img alt='food truck pic' src={props.image}/>
            <p>{props.location}</p>
            <p>{props.departTime}</p>
            <div>
            <button>Update</button>
            <button onClick={handleDelete}>Delete</button>
            </div>
        </div>
    )
}
