import React, {useState} from 'react'
import { Link } from 'react-router-dom'
import axiosWithAuth from '../components/axiosWithAuth'
import {useHistory} from 'react-router-dom';
import { MainPageContainer, FormContainer } from '../styled-components'


export default function Login({setIsLoggedIn}) {
    const [credentials, setCredentials] = useState({
        username: '',
        password: ''
    });

    const [isLoading, setIsLoading] = useState(false);
    const {push} = useHistory();

    const handleChange = e => {
        setCredentials({...credentials, [e.target.name]: e.target.value })

    };




    const handleSubmit = e => {
        e.preventDefault();
        setIsLoading(true);
            axiosWithAuth()
        .post('/api/diner-auth/login', credentials)
        .then(res => {
            console.log("login, post", res)
            localStorage.setItem("token", res.data.token)
            localStorage.setItem('Logged in', true)
            setIsLoading(false);
            push('/Diner/DashBoard')
        })
        .catch(err => {
            console.log("err", err, credentials)
            setIsLoading(false);
        })

        




    }


	return (
        <MainPageContainer>
		<div className='UserLogin'>
            <h1> SIGN IN </h1>

			<FormContainer onSubmit={handleSubmit}>
					<input
						type="text"
						placeholder="User Name"
						name="username"
                        value={credentials.username}
                        onChange={handleChange} 
					/>

					<input
						type="password"
						placeholder="Password"
						name="password"
                        value={credentials.password}
                        onChange={handleChange} 
                    />


                    {isLoading ? 'loading...' : <button type="submit">Log in</button>}

					<p> Not a user? </p>
					<Link to="/Signup">Sign up!</Link>
                </FormContainer>

        </div>
        </MainPageContainer>

	)
}
