import React, {useState, useEffect} from 'react'
import axiosWithAuth from '../axiosWithAuth'
import {useHistory} from 'react-router-dom'


const initialItem = {
    truckName: '',
    imgOfTruck: '',
    currentLocation: '',
    customerRatings: '',
    departTime: ''
}

export default function TruckDetails({match, handleSubmit}) {
    const [item, setItem] = useState(initialItem);
    console.log(match.params.id)
    console.log(item)

    useEffect(()=>{
        axiosWithAuth().get(`/api/trucks/${match.params.id}`)
        .then(res =>{
            console.log(res.data.data)
            setItem(res.data.data)


        })
        .catch(err =>{
            console.log('No Data coming in', err)
        })

        const handleSubmit = e => {
            e.preventDefault();
            axiosWithAuth().put(`/api/trucks/${match.params.id}`, item)
            .then(res =>{
                setItem(res.data.data)
            })
            .catch(err =>{
                console.log('No Data coming in', err)
            })
    
          };

    },[match.params.id])

    const changeHandler = e => {
        e.persist();
        let value = e.target.value;
         {
             
        }
    
        setItem({
          ...item,
          [e.target.name]: value
        });
      };
    // const {push } = useHistory();
      
    


    return (
        <div>
        <h1>EDIT DETAILS</h1>
            <form onSubmit={handleSubmit}>
        <input
          type="string"
          name="truckName"
          onChange={changeHandler}
          placeholder="truckName"
          value={item.truckName}
        />
        <div className="baseline" />

        <input
          type="string"
          name="imgOfTruck"
          onChange={changeHandler}
          placeholder="imgOfTruck"
          value={item.imgOfTruck}
        />
        <div className="baseline" />

        <input
          type="text"
          name="currentLocation"
          onChange={changeHandler}
          placeholder="Image"
          value={item.currentLocation}
        />
        <div className="baseline" />

        <input
          type="text"
          name="customerRatings"
          onChange={changeHandler}
          placeholder="Customer Rating"
          value={item.customerRatings}
        />
        <div className="baseline" />

        <input
          type="text"
          name="departTime"
          onChange={changeHandler}
          placeholder="Depart Time"
          value={item.departTime}
        />
        <div className="baseline" />
        <button >Update</button>
        </form>
        </div>
    )
}
