import React, {useContext, useState} from 'react'
import { DinerContext } from '../../Contexts/DinerContext'
import {Link, useHistory} from 'react-router-dom'
import axiosWithAuth from '../axiosWithAuth'


export default function TruckCard(props) {
    const {push} = useHistory();
const [dinersList, dinerTrucks] = useContext(DinerContext)
const [newTrucks, setNewTrucks] =useState();
console.log(newTrucks)


const handleDelete = e => {
    axiosWithAuth().delete(`/api/trucks/${props.id}`)
    .then(res =>{
        console.log(res)
        setNewTrucks(dinerTrucks);

    })
    .catch (err =>{
        console.log(err,'no data coming in')
    })
}

    return (
        <div>
            <h1>{props.name}</h1>
            <img src={props.image}/>
            <Link to ={`/Diner/DashBoard/truck/${props.id}`}><button>Edit</button></Link>
            <button onClick={handleDelete}>Delete</button>
        </div>
    )
}
