import React, {useState, useEffect} from 'react'
import axiosWithAuth from '../axiosWithAuth'
import {useHistory} from 'react-router-dom'

const newInitialItem = {
  truckName: '',
  imgOfTruck: '',
  currentLocation: '',
  customerRatings: '',
  departTime: ''
}

export default function TruckForm(props) {
  const [newItem, setNewItem] = useState(newInitialItem)
  console.log(props.data)
  setNewItem(props.data)

  const {push } = useHistory();
  const handleSubmit = e => {
    e.preventDefault();
    // axiosWithAuth().put(`/api/trucks/${match.params.id}`, item)
    // .then(res =>{
    //     console.log(res)
    //     setItem(res.data.data)
    //     push('//Diner/DashBoard')
    // })
    // .catch(err =>{
    //     console.log('No Data coming in', err)
    // })

  };


  const changeHandler = e => {
    e.persist();
    let value = e.target.value;
     {
         
    }

    setNewItem({
      ...props.data,
      [e.target.name]: value
    });
  };



    return (
        <div>
        <h1>EDIT DETAILS</h1>
        <form onSubmit={handleSubmit}>
    <input
      type="string"
      name="truckName"
      onChange={changeHandler}
      placeholder="turckName"
      value={props.truckName}
    />
    <div className="baseline" />

    <input
      type="string"
      name="imgOfTruck"
      onChange={changeHandler}
      placeholder="imgOfTruck"
      value={props.imgOfTruck}
    />
    <div className="baseline" />

    <input
      type="text"
      name="currentLocation"
      onChange={changeHandler}
      placeholder="Image"
      value={props.currentLocation}
    />
    <div className="baseline" />

    <input
      type="text"
      name="customerRatings"
      onChange={changeHandler}
      placeholder="Customer Rating"
      value={props.customerRatings}
    />
    <div className="baseline" />

    <input
      type="text"
      name="departTime"
      onChange={changeHandler}
      placeholder="Depart Time"
      value={props.departTime}
    />
    <div className="baseline" />
    <button type='submit'>Update</button>
    </form>
        </div>
    )
}
