import React, {useState, useContext, useEffect} from 'react'
import {Link } from 'react-router-dom'
import axiosWithAuth from '../axiosWithAuth'
import OpTruckCard from './OpTruckCard'



export default function OperatorTrucks({match}) {
    const [item, setItem] = useState([]);
    const [truck, setTrucks] = useState([]);


    useEffect(()=>{

        
        axiosWithAuth().get(`/api/ops/${match.params.id}`)
        .then(res =>{
            console.log(res.data.data)
            setItem(res.data.data)
        })
        .catch(err =>{
            console.log(err, 'No data Coming in!')
        })

        axiosWithAuth().get(`/api/ops/${match.params.id}/trucks`)
        .then(res =>{
            console.log(res.data.operatorsTrucks)
            setTrucks(res.data.operatorsTrucks)

        })
        .catch(err =>{
            console.log(err, 'No data Coming in!')
        })

    },[match.params.id])

    console.log(props.user)
    const {push} = useHistory();
    const handleDelete = e => {
        axiosWithAuth().delete(`/api/trucks/${truck.id}`)
        .then(res =>{
            // window.location.reload(true)
            push(`/Operator/Dashboard/trucks/${item.user}`)
            console.log(res);
    
        })
        .catch (err =>{
            console.log(err,'no data coming in')
        })
    }




    return (
        <div>
            <h1>Welcome: {item.username}</h1>
            <Link to ={`/Operator/Dashboard/trucks/add/${item.id}`}><h2>Click Here: To add a truck</h2></Link>
            <h2>Here are your Current Trucks</h2>
            {truck.map(truck =>{
                return (
                    <OpTruckCard
                    user={item.id}
                    key={truck.id}
                    id={truck.id}
                    truckName={truck.truckName}
                    image={truck.imgOfTruck}
                    location={truck.currentLocation}
                    departTime={truck.departTime}

                    />
                )
            })}
            <button onClick={handleDelete}>Delete</button>
        </div>
    )
}
